requestBody.schema:
{
  "required": [
    "user_address",
    "broker_id"
  ],
  "type": "object",
  "properties": {
    "chain_id": {
      "type": "string",
      "description": "The chain ID that the test USDC should be deposited to. Not applicable to Solana faucet."
    },
    "user_address": {
      "type": "string",
      "description": "The address of the user account. Use `Zions51qQNUgWNyp4JegUFoMUpgFx43jBUsYmHtDPdr` for Solana address."
    },
    "broker_id": {
      "type": "string",
      "description": "Builder ID of an account"
    }
  }
}

{
  "type": "object",
  "properties": {
    "symbol": {
      "type": "string"
    },
    "algo_type": {
      "type": "string",
      "description": "`STOP`/`TP_SL`/`POSITIONAL_TP_SL`/`BRACKET`/`BRACKET + TP_SL`/`TRAILING_STOP`"
    },
    "client_order_id": {
      "type": "string",
      "description": "36 length, accepts hyphen but cannot be the first character, default: null"
    },
    "type": {
      "type": "string",
      "description": "`LIMIT` / `MARKET`, required if `algo_type` = `STOP`"
    },
    "price": {
      "type": "number",
      "description": "Optional for `TP_SL` and `POSTIONAL_TP_SL`"
    },
    "quantity": {
      "type": "number",
      "description": "For `MARKET`/`ASK`/`BID` order, if order_amount is given, it is not required. Not required if type is `POSITIONAL_TP_SL`."
    },
    "trigger_price_type": {
      "type": "string",
      "description": "Only `MARK_PRICE` is available for now."
    },
    "trigger_price": {
      "type": "number"
    },
    "reduce_only": {
      "type": "boolean"
    },
    "visible_quantity": {
      "type": "boolean",
      "description": "Default false"
    },
    "side": {
      "type": "string",
      "description": "`SELL`/`BUY`, required if `STOP` type"
    },
    "order_tag": {
      "type": "string"
    },
    "child_orders": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "symbol": {
            "type": "string"
          },
          "algo_type": {
            "type": "string"
          },
          "child_orders": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/CreateAlgoOrderChildRequest"
            },
            "description": "Array of `CreateAlgoOrderChildRequest`"
          }
        }
      }
    },
    "activatedPrice": {
      "type": "string",
      "description": "Activated price for algoType=`TRAILING_STOP`"
    },
    "callbackRate": {
      "type": "string",
      "description": "Callback rate, only for algoType=`TRAILING_STOP`, i.e. the value = 0.1 represent to 10%."
    },
    "callbackValue": {
      "type": "string",
      "description": "Callback value, only for algoType=`TRAILING_STOP`, i.e. the value = 100"
    }
  },
  "example": [
    {
      "symbol": "PERP_ETH_USDC",
      "algo_type": "BRACKET",
      "quantity": 0.0032,
      "side": "BUY",
      "type": "LIMIT",
      "price": 3415.9,
      "child_orders": [
        {
          "symbol": "PERP_ETH_USDC",
          "algo_type": "POSITIONAL_TP_SL",
          "child_orders": [
            {
              "symbol": "PERP_ETH_USDC",
              "algo_type": "TAKE_PROFIT",
              "side": "SELL",
              "type": "CLOSE_POSITION",
              "trigger_price": 3518.4,
              "reduce_only": true
            },
            {
              "symbol": "PERP_ETH_USDC",
              "algo_type": "STOP_LOSS",
              "side": "SELL",
              "type": "CLOSE_POSITION",
              "trigger_price": 3313.4,
              "reduce_only": true
            }
          ]
        }
      ]
    },
    {
      "symbol": "PERP_NEAR_USDC",
      "algo_type": "TP_SL",
      "quantity": 5.5,
      "trigger_price_type": "MARK_PRICE",
      "child_orders": [
        {
          "symbol": "PERP_NEAR_USDC",
          "algo_type": "TAKE_PROFIT",
          "side": "SELL",
          "type": "MARKET",
          "trigger_price": 3.365,
          "reduce_only": true
        },
        {
          "symbol": "PERP_NEAR_USDC",
          "algo_type": "STOP_LOSS",
          "side": "SELL",
          "type": "MARKET",
          "trigger_price": 3.36,
          "reduce_only": true
        }
      ]
    },
    {
      "symbol": "PERP_NEAR_USDC",
      "algo_type": "POSITIONAL_TP_SL",
      "trigger_price_type": "MARK_PRICE",
      "child_orders": [
        {
          "symbol": "PERP_NEAR_USDC",
          "algo_type": "TAKE_PROFIT",
          "side": "SELL",
          "type": "CLOSE_POSITION",
          "trigger_price_type": "MARK_PRICE",
          "trigger_price": 4.05,
          "reduce_only": true
        },
        {
          "symbol": "PERP_NEAR_USDC",
          "algo_type": "STOP_LOSS",
          "side": "SELL",
          "type": "CLOSE_POSITION",
          "trigger_price_type": "MARK_PRICE",
          "trigger_price": 3.95,
          "reduce_only": true
        }
      ]
    }
  ],
  "required": [
    "symbol",
    "algo_type",
    "quantity",
    "side",
    "type",
    "child_orders"
  ]
}

