const url = 'https://api.orderly.org/v1/client/add_sub_account';
const timestamp = Date.now().toString();
const signature = signMessage(timestamp + 'POST' + '/v1/client/add_sub_account' + JSON.stringify(body));

fetch(url, {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'orderly-account-id': 'YOUR_ACCOUNT_ID',
    'orderly-key': 'YOUR_PUBLIC_KEY',
    'orderly-signature': signature,
    'orderly-timestamp': timestamp
  },
  body: JSON.stringify(body)
}).then(res => res.json()).then(console.log);

