};

import React, { forwardRef, useState } from "react";
import { CheckboxIcon } from "@orderly.network/ui";

interface AccessibleCheckboxProps {
  label: string;
  checked?: boolean;
  onChange?: (checked: boolean) => void;
}

export const AccessibleCheckbox = forwardRef<HTMLButtonElement, AccessibleCheckboxProps>(
  ({ label, checked: controlledChecked, onChange }, ref) => {
    const [internalChecked, setInternalChecked] = useState(false);
    const isControlled = controlledChecked !== undefined;
    const checked = isControlled ? controlledChecked : internalChecked;

    const toggle = () => {
      const newState = !checked;
      if (!isControlled) setInternalChecked(newState);
      onChange?.(newState);
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
      if (e.key === "Enter" || e.key === " ") {
        e.preventDefault();
        toggle();
      }
    };

    return (
      <button
        ref={ref}
        role="checkbox"
        aria-checked={checked}
        onClick={toggle}
        onKeyDown={handleKeyDown}
        className="flex items-center gap-3 group focus:outline-none"
      >
        <div
          className={`w-5 h-5 rounded border flex items-center justify-center transition-all duration-200 ${
            checked
              ? "bg-blue-500 border-blue-500 shadow-[0_0_10px_rgba(59,130,246,0.5)]"
              : "border-gray-600 bg-gray-800 group-hover:border-gray-500"
          } group-focus:ring-2 group-focus:ring-blue-500/50`}
        >
          <CheckboxIcon
            className={`text-white transition-transform duration-200 ${
              checked ? "scale-100" : "scale-0"
            }`}
          />
        </div>
        <span className="text-sm text-gray-300 select-none">{label}</span>
      </button>
    );
  }
);

