    // Example: Calculate quantity based on 50% of available balance
    // In a real app, fetch available balance from useAccountInfo
    const mockAvailable = 1000; 
    const newQty = (val / 100) * mockAvailable;
    updateQuantity(newQty.toFixed(2));
  };

  return (
    <div className="p-4 border rounded bg-gray-50">
      <div className="flex justify-between mb-2">
        <span className="text-sm font-bold">Size %</span>
        <span className="text-sm text-blue-600">{quantity}</span>
      </div>

      <SliderTip 
        value={sliderVal} 
        formatter={(v) => `${v}%`}
        visible={true}
      >
        <input
          type="range"
          min="0"
          max="100"
          value={sliderVal}
          onChange={(e) => handleSliderChange(Number(e.target.value))}
          className="w-full h-1.5 bg-gray-300 rounded-lg appearance-none cursor-pointer accent-blue-600"
        />
      </SliderTip>
    </div>
  );
}

