import React, { useState } from "react";
import { TableBody, TableCell } from "@orderly.network/ui";
import { useOrderEntity } from "@orderly.network/hooks";

export function AdvancedTableBody() {
  const { orders } = useOrderEntity();
  const [selectedId, setSelectedId] = useState<number | null>(null);

  const handleRowClick = (id: number) => {
    setSelectedId(id === selectedId ? null : id);
  };

  return (
    <TableBody>
      {orders?.map((order) => {
        const isSelected = selectedId === order.order_id;
        const sideColor = order.side === "BUY" ? "text-green-500" : "text-red-500";

        return (
          <tr 
            key={order.order_id} 
            onClick={() => handleRowClick(order.order_id)}
            className={`cursor-pointer transition-colors ${
              isSelected ? "bg-blue-900/30" : "hover:bg-gray-800/50"
            }`}
          >
            <TableCell>{order.symbol}</TableCell>
            <TableCell className={sideColor}>{order.side}</TableCell>
            <TableCell>{order.average_price || "-"}</TableCell>
            <TableCell>{order.remaining_quantity}</TableCell>
          </tr>
        );
      })}
    </TableBody>
  );
}

