export const RoiAdvancedExample = () => {
  const { accountInfo, loading } = useAccountInfo();

  // Mock calculation logic for demonstration
  // In a real app, this might come from useStatisticsDaily or similar
  const calculateDailyRoi = () => {
    if (!accountInfo) return 0;
    const { total_equity, yesterday_equity } = accountInfo;
    if (!yesterday_equity || yesterday_equity === 0) return 0;
    return (total_equity - yesterday_equity) / yesterday_equity;
  };

  const dailyRoi = calculateDailyRoi();

  return (
    <div className="p-6 bg-card rounded-lg shadow-sm border border-border">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm text-gray-400">Daily ROI (24h)</p>
          {loading ? (
            <div className="h-6 w-20 bg-gray-700 animate-pulse rounded mt-1" />
          ) : (
            <div className="text-2xl font-bold mt-1">
              <NumTypeRoi 
                value={dailyRoi} 
                options={{ decimals: 2, suffix: "%" }} 
              />
            </div>
          )}
        </div>

        <div className="text-right">
           <p className="text-xs text-gray-500">Total Equity</p>
           <p className="font-mono">
             ${accountInfo?.total_equity?.toFixed(2) || "0.00"}
           </p>
        </div>
      </div>
    </div>
  );
};

