export const AdvancedLeverageInput = ({ symbol }) => {
  const { leverage, setLeverage, maxLeverage, error } = useOrderEntry(symbol);
  const [localValue, setLocalValue] = React.useState(leverage);

  const handleCommit = (val) => {
