import React from "react";
import { LTVRiskTooltipUI } from "@orderly.network/ui-order-entry";
import { useAccountInfo } from "@orderly.network/hooks";

export const AdvancedLTVTooltip = () => {
  const { accountInfo } = useAccountInfo();
  const currentLTV = accountInfo?.margin_ratio || 0;
  const threshold = 0.85;

  // Determine risk level for styling
  const getRiskColor = (ltv: number) => {
    if (ltv < 0.5) return "text-green-500";
    if (ltv < 0.75) return "text-yellow-500";
    return "text-red-500";
  };

  return (
    <div className="bg-gray-800 p-4 rounded-lg shadow-lg border border-gray-700">
      <div className="flex justify-between items-center mb-2">
        <h4 className="font-semibold text-white">Margin Health</h4>
        <span className={`text-xs font-bold ${getRiskColor(currentLTV)}`}>
          {currentLTV.toFixed(2)} LTV
        </span>
      </div>

      <div className="relative">
        <LTVRiskTooltipUI
          ltv={currentLTV}
          liquidationThreshold={threshold}
          markPrice={accountInfo?.equity || 1000}
          liquidationPrice={1000 * (threshold / currentLTV)}
        />
      </div>

      <p className="mt-2 text-[10px] text-gray-500">
        Maintain LTV below {threshold * 100}% to avoid liquidation.
      </p>
    </div>
  );
};

