import React from "react";
import { useAccountInfo } from "@orderly.network/hooks";
import { useOrderEntry } from "@orderly.network/hooks";

export const EffectiveFeeUI = () => {
  const { accountInfo } = useAccountInfo();
  const { symbol } = useOrderEntry();

  // Base rates (example defaults, usually fetched from config)
  const BASE_MAKER = 0.0002; // 0.02%
  const BASE_TAKER = 0.0005; // 0.05%

  const currentMaker = accountInfo?.fee_rates?.maker ?? 0;
  const currentTaker = accountInfo?.fee_rates?.taker ?? 0;

  const makerDiscount = ((BASE_MAKER - currentMaker) / BASE_MAKER) * 100;
  const takerDiscount = ((BASE_TAKER - currentTaker) / BASE_TAKER) * 100;

  return (
    <div className="fee-card">
      <h4>Fee Rates ({symbol})</h4>

      <div className="fee-group">
        <div className="fee-item">
          <span className="label">Maker</span>
          <span className="rate">{(currentMaker * 100).toFixed(4)}%</span>
          {makerDiscount > 0 && (
            <span className="discount">-{makerDiscount.toFixed(0)}% VIP</span>
