import React, { useEffect } from "react";
import { ReduceOnlySwitch } from "@orderly.network/ui-order-entry";
import { OrderEntryProvider } from "@orderly.network/ui-order-entry";
import { useOrderEntry, usePositionClose } from "@orderly.network/hooks";

const AdvancedSwitch = () => {
  const { state, setState } = useOrderEntry();
  const { position } = usePositionClose();

  // Disable reduce-only if no position exists
  const hasPosition = !!position && Math.abs(position.quantity) > 0;

  useEffect(() => {
    if (!hasPosition && state.reduceOnly) {
      setState({ reduceOnly: false });
    }
  }, [hasPosition, state.reduceOnly, setState]);

  return (
    <div className="bg-gray-800 p-4 rounded-xl shadow-lg border border-gray-700">
      <div className="flex items-center justify-between mb-4">
        <label className="text-sm font-medium text-gray-200">
          Reduce Only Order
        </label>
        <ReduceOnlySwitch
          disabled={!hasPosition}
          checked={state.reduceOnly}
          onCheckedChange={(val) => setState({ reduceOnly: val })}
          className="data-[disabled]:opacity-50"
        />
      </div>

      {!hasPosition && (
        <div className="text-xs text-yellow-500 bg-yellow-500/10 p-2 rounded">
          No open position found. Reduce-only is disabled.
        </div>
      )}
    </div>
  );
};

