function OrderManager() {
  const { state } = useOrderStore();
  const { cancelOrder } = useOrderEntity();

  // Custom handler to cancel all open orders
  const handleCancelAll = async () => {
    const openOrders = state.orders.filter(o => o.status === "OPEN");
    await Promise.all(openOrders.map(order => cancelOrder(order)));
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100%" }}>
      <div style={{ padding: "10px", borderBottom: "1px solid #333" }}>
        <Button onClick={handleCancelAll} size="small">
          Cancel All
        </Button>
      </div>
      <div style={{ flex: 1 }}>
        <MobileOrderListWidget 
          symbol="PERP_SOL_USDC"
          type="open"
          onCancel={(order) => cancelOrder(order)}
        />
      </div>
    </div>
  );
}

