import React, { useState } from "react";
import { CloseAllPositionsWidget } from "@orderly.network/ui-positions";
import { usePositionClose, usePositions } from "@orderly.network/hooks";

export const AdvancedCloseAll = () => {
  const { positions } = usePositions();
  const { closeAll } = usePositionClose();
  const [status, setStatus] = useState<"idle" | "closing">("idle");

  const handleClose = async () => {
    setStatus("closing");
    try {
      // Custom logic using the hook alongside the widget
      await closeAll({ symbol: "PERP_BTC_USDC" });
    } catch (e) {
      console.error("Close failed", e);
    } finally {
      setStatus("idle");
    }
  };

  return (
    <div className="p-6 border rounded-lg bg-gray-50">
      <div className="flex justify-between items-center mb-4">
        <h3>Advanced Control</h3>
        <span className="text-sm text-gray-500">
          Open Positions: {positions.length}
        </span>
      </div>

      <CloseAllPositionsWidget 
        symbol="PERP_BTC_USDC"
        onLoadingChange={(isLoading) => setStatus(isLoading ? "closing" : "idle")}
      />

      {status === "closing" && (
        <p className="mt-2 text-blue-500 text-sm">Processing closure...</p>
      )}
    </div>
  );
};

