interface DisplayCellProps {
  value: number;
  type: "price" | "size";
  symbol?: string;
}

const DisplayCell = ({ value, type, symbol }: DisplayCellProps) => {
  const { symbol: contextSymbol } = useSymbolInfo();
  const activeSymbol = symbol || contextSymbol;

  if (!activeSymbol) {
    return <span>-</span>;
  }

  return (
    <span className="font-mono">
      <NumeralWithCtx 
        value={value} 
        mode={type} 
        // Explicitly passing context if not available globally
        context={{ symbol: activeSymbol }} 
      />
    </span>
  );
};

