import React from "react";
import { ReversePositionWidget } from "@orderly.network/ui-positions";
import { usePositionClose } from "@orderly.network/hooks";

export const AdvancedReverse = ({ symbol }: { symbol: string }) => {
  // Check if user actually has a position to reverse
  const { position } = usePositionClose(symbol);

  // If position size is 0 or undefined, don't show the widget
  if (!position || Math.abs(position.quantity) === 0) {
    return (
      <div className="p-4 border border-dashed border-gray-600 rounded text-gray-500 text-center">
        No open position to reverse for {symbol}
      </div>
    );
  }

  return (
    <div className="flex flex-col gap-2">
      <div className="flex justify-between items-center text-sm text-gray-400">
        <span>Current Side: <strong className={position.side === 'LONG' ? 'text-green-500' : 'text-red-500'}>{position.side}</strong></span>
        <span>Size: {position.quantity}</span>
      </div>

      <div className="bg-gray-900 p-4 rounded-xl border border-gray-700 shadow-lg">
        <ReversePositionWidget 
          symbol={symbol}
          // You can often pass custom button classes or config depending on SDK version
          // Here we assume standard usage wrapped in custom UI
        />
      </div>
      <p className="text-xs text-gray-500 mt-2">
        *This will close your {position.side} and open a {position.side === 'LONG' ? 'SHORT' : 'LONG'} of the same size.
      </p>
    </div>
  );
};

