import { useMemo } from "react";
import { BottomNav } from "@orderly.network/ui-scaffold";
import { Home, BarChart, User, LogIn } from "lucide-react";
import { useAccountInfo } from "@orderly.network/hooks";

export default function AdvancedBottomNav() {
  const { state } = useAccountInfo();

  const navItems = useMemo(() => {
    const baseItems = [
      { id: "home", icon: Home, label: "Home" },
      { id: "markets", icon: BarChart, label: "Markets" },
    ];

    if (state === "connected") {
      baseItems.push({ id: "profile", icon: User, label: "Profile" });
    } else {
      baseItems.push({ id: "login", icon: LogIn, label: "Connect" });
    }

    return baseItems;
  }, [state]);

  return (
    <BottomNav
      activeTab="home"
      onTabChange={(id) => console.log(`Tab changed: ${id}`)}
      items={navItems}
    />
  );
}

