import React, { useState } from "react";
import { TPSL } from "@orderly.network/ui-tpsl";
import { useOrderEntry, useAccountInfo } from "@orderly.network/hooks";

export const AdvancedTPSL = () => {
  const { symbol, side } = useOrderEntry();
  const { positionMode } = useAccountInfo();
  const [tpslValues, setTpslValues] = useState({ tp: '', sl: '' });

  const handleChange = (type: 'tp' | 'sl', value: string) => {
    setTpslValues(prev => ({ ...prev, [type]: value }));
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center gap-2 text-xs text-gray-400">
        <span>Mode: {positionMode}</span>
      </div>

      <TPSL
        symbol={symbol}
        side={side}
        tpPrice={tpslValues.tp}
        slPrice={tpslValues.sl}
        onTpChange={(val) => handleChange('tp', val)}
        onSlChange={(val) => handleChange('sl', val)}
        showROE
      />

      <div className="text-right text-xs text-gray-500">
        Estimated PnL updates based on entry price.
      </div>
    </div>
  );
