import React, { useState } from "react";
import { TPSLWidget } from "@orderly.network/ui-tpsl";
import { useOrderEntry, useAccountInfo } from "@orderly.network/hooks";
import { Dialog } from "@orderly.network/ui";

export const AdvancedTPSL = ({ position, onClose }) => {
  const { submitOrder } = useOrderEntry();
  const { state } = useAccountInfo();
  const [loading, setLoading] = useState(false);

  const handleSubmission = async (tpslParams) => {
    if (!state) {
      alert("Please connect wallet first");
      return;
    }
    setLoading(true);
    try {
      // Example: Placing TP order
      if (tpslParams.tp_price) {
        await submitOrder({
          symbol: position.symbol,
          side: position.side === "LONG" ? "SELL" : "BUY",
          order_type: "LIMIT",
          reduce_only: true,
          price: tpslParams.tp_price,
          quantity: Math.abs(position.position_qty)
        });
      }
      // Repeat for SL...
      onClose();
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={!!position} onClose={onClose}>
      <TPSLWidget
        position={position}
        onSubmit={handleSubmission}
        loading={loading}
      />
    </Dialog>
  );
};

