import React, { useState, useMemo } from "react";
import { LtvWidget } from "@orderly.network/ui-transfer";
import { useAccountInfo } from "@orderly.network/hooks";

export const LtvWidgetAdvanced = () => {
  const { accountInfo } = useAccountInfo();
  const [depositAmount, setDepositAmount] = useState(0);

  const metrics = useMemo(() => {
    if (!accountInfo) return { current: 0, projected: 0, max: 0.85 };

    const collateral = Number(accountInfo.total_collateral_val);
    const debt = Number(accountInfo.total_position_notional);
    const currentLtv = collateral > 0 ? debt / collateral : 0;

    // Simulate projection: Adding collateral reduces LTV
    const newCollateral = collateral + depositAmount;
    const projectedLtv = newCollateral > 0 ? debt / newCollateral : 0;

    return { current: currentLtv, projected: projectedLtv, max: 0.85 };
  }, [accountInfo, depositAmount]);

  return (
    <div className="p-4 bg-gray-50 rounded-lg">
      <div className="mb-4">
        <label className="text-xs text-gray-500">Simulate Deposit (USDC)</label>
        <input 
          type="number" 
          value={depositAmount} 
          onChange={(e) => setDepositAmount(Number(e.target.value))}
          className="w-full p-2 border rounded mt-1"
        />
      </div>
      <LtvWidget 
        value={metrics.current} 
        maxValue={metrics.max} 
        projectedValue={metrics.projected}
      />
    </div>
  );
};

