import React, { useMemo } from 'react';
import { useAccountInfo, useChainInfo } from '@orderly.network/hooks';
import { Decimal } from 'decimal.js';

interface Props {
  amount: string | number;
  token?: string;
}

export const CollateralContribution = ({ amount, token = 'USDC' }: Props) => {
  const { data: account } = useAccountInfo();
  const { data: chainInfo } = useChainInfo();

  const tokenInfo = chainInfo?.tokens.find(t => t.symbol === token);
  const decimals = tokenInfo?.decimals || 6;

  const displayValue = useMemo(() => {
    try {
      const val = new Decimal(amount).toDecimalPlaces(decimals);
      return val.toFixed(decimals);
    } catch (e) {
      return '0.00';
    }
  }, [amount, decimals]);

  const isSufficient = useMemo(() => {
    if (!account) return true;
    return new Decimal(account.freeBalance).gte(amount);
  }, [account, amount]);

  return (
    <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
      <div className="flex justify-between items-center mb-2">
        <span className="text-sm font-medium text-gray-700">Contribution</span>
        <span className={`text-xs px-2 py-1 rounded ${isSufficient ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
          {isSufficient ? 'Sufficient Balance' : 'Insufficient Balance'}
        </span>
      </div>
      <div className="text-2xl font-mono font-semibold text-gray-900">
        {displayValue} <span className="text-lg text-gray-500">{token}</span>
      </div>
    </div>
  );
};

