import React from 'react';
import { useAccountInfo } from '@orderly.network/hooks';

export const CollateralRatioUI = () => {
  const { data: account } = useAccountInfo();
  const ratio = account?.marginRatio || 0;

  // Thresholds
  const DANGER = 1.1;
  const WARNING = 1.5;
  const SAFE = 3.0;

  const getStatus = () => {
    if (ratio < DANGER) return { label: 'Liquidation Risk', color: 'bg-red-500', text: 'text-red-600' };
    if (ratio < WARNING) return { label: 'High Risk', color: 'bg-yellow-500', text: 'text-yellow-600' };
    return { label: 'Safe', color: 'bg-green-500', text: 'text-green-600' };
  };

  const status = getStatus();
  const percentage = Math.min((ratio / SAFE) * 100, 100);

  return (
    <div className="w-full">
      <div className="flex justify-between items-center mb-1">
        <span className="text-sm font-medium text-gray-700">Health Factor</span>
        <span className={`text-sm font-bold ${status.text}`}>{status.label}</span>
      </div>

      <div className="w-full bg-gray-200 rounded-full h-2.5 overflow-hidden">
        <div 
          className={`h-2.5 rounded-full transition-all duration-500 ${status.color}`} 
          style={{ width: `${percentage}%` }}
        ></div>
      </div>

      <div className="flex justify-between mt-1 text-xs text-gray-500">
        <span>0x</span>
        <span className="font-mono text-base font-bold text-gray-800">{ratio.toFixed(3)}x</span>
        <span>{SAFE}x+</span>
      </div>
    </div>
  );
};

