import { ConvertAction } from "@orderly.network/ui-transfer";
import { useAccountInfo } from "@orderly.network/hooks";
import { useState } from "react";

export const AdvancedConvert = () => {
  const { accountInfo } = useAccountInfo();
  const [amount, setAmount] = useState("");
  const [status, setStatus] = useState<"idle" | "loading" | "success" | "error">("idle");

  const balance = accountInfo?.free_balance || 0;
  const isValid = Number(amount) > 0 && Number(amount) <= balance;

  const onExecute = async () => {
    if (!isValid) return;
    setStatus("loading");
    try {
      // API call logic here
      await new Promise((r) => setTimeout(r, 1500));
      setStatus("success");
    } catch (e) {
      setStatus("error");
    }
  };

  return (
    <div className="p-4 bg-gray-50 rounded">
      <div className="mb-2 text-sm">Available: {balance}</div>
      <input
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        className="w-full p-2 mb-3 border rounded"
      />
      <ConvertAction
        onClick={onExecute}
        disabled={!isValid || status === "loading"}
        loading={status === "loading"}
      >
        {status === "success" ? "Converted!" : "Execute Convert"}
      </ConvertAction>
    </div>
  );
};

