export const AdvancedDepositStatus = () => {
  // Fetch deposit history to get real-time status
  const { data: transfers, isLoading } = useTransferHistory({ type: "DEPOSIT" });

  // Get the most recent deposit
  const latestDeposit = transfers && transfers.length > 0 ? transfers[0] : null;

  if (isLoading) {
    return <div className="text-gray-500">Loading status...</div>;
  }

  if (!latestDeposit) {
    return <div className="text-gray-500">No recent deposits found.</div>;
  }

  return (
    <div className="flex flex-col gap-2">
      <div className="flex justify-between items-center">
        <span className="text-gray-400 text-sm">Latest Deposit</span>
        <span className="text-white font-mono">{latestDeposit.amount} {latestDeposit.token}</span>
      </div>

      <div className="flex items-center gap-2">
        <DepositStatus status={latestDeposit.status as any} />
        <span className="text-xs text-gray-500">
          {new Date(latestDeposit.created_time).toLocaleTimeString()}
        </span>
      </div>
    </div>
  );
};

