import React, { useState, useEffect } from 'react';
import { useAccountInfo, useChain, useTransferHistory } from '@orderly.network/hooks';
import { WithdrawWarningMessage } from '@orderly.network/ui-transfer';

export const AdvancedWithdraw = () => {
  const { data: account, refetch } = useAccountInfo();
  const { chains } = useChain();
  const { data: history } = useTransferHistory({ type: 'withdraw' });

  const [amount, setAmount] = useState('');
  const [chainId, setChainId] = useState('');
  const [status, setStatus] = useState('idle'); // idle, loading, success, error
  const [error, setError] = useState(null);

  useEffect(() => {
    if (chains?.length) setChainId(chains[0].id);
  }, [chains]);

  const balance = account?.free_balance || 0;

  const handleWithdraw = async () => {
    setError(null);
    if (parseFloat(amount) > balance) {
      setError('Insufficient balance');
      return;
    }

    setStatus('loading');
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1500));
      setStatus('success');
      setAmount('');
      refetch(); // Refresh balance
    } catch (err) {
      setError('Withdrawal failed. Please try again.');
      setStatus('error');
    }
  };

  return (
    <div className="max-w-md mx-auto space-y-6">
      <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
        <h2 className="text-xl font-bold mb-6">Withdraw Funds</h2>

        {error && (
          <div className="mb-4 p-3 bg-red-50 text-red-600 text-sm rounded-lg">
            {error}
          </div>
        )}

        <div className="space-y-4">
          <div>
            <label className="text-xs font-semibold text-gray-500 uppercase">Amount</label>
            <div className="flex items-center mt-1 p-1 border rounded-lg focus-within:ring-2 focus-within:ring-blue-500">
              <input 
                type="number" 
                value={amount} 
                onChange={e => setAmount(e.target.value)}
                className="flex-1 p-2 outline-none text-lg"
                placeholder="0.00"
              />
              <span className="px-3 text-gray-500 font-medium">USD</span>
            </div>
            <div className="text-right text-xs text-gray-400 mt-1">
              Available: {balance.toFixed(2)}
            </div>
          </div>

          <div>
            <label className="text-xs font-semibold text-gray-500 uppercase">To Chain</label>
            <div className="grid grid-cols-3 gap-2 mt-2">
              {chains?.map(c => (
                <button
                  key={c.id}
                  onClick={() => setChainId(c.id)}
                  className={`p-2 text-sm rounded-lg border transition ${
                    chainId === c.id ? 'border-blue-500 bg-blue-50 text-blue-700' : 'border-gray-200'
                  }`}
                >
                  {c.name}
                </button>
              ))}
            </div>
          </div>

          <WithdrawWarningMessage className="text-xs text-orange-600 bg-orange-50 p-3 rounded" />

          <button
            onClick={handleWithdraw}
            disabled={status === 'loading' || !amount}
            className="w-full py-3 bg-blue-600 text-white rounded-lg font-bold disabled:opacity-50 disabled:cursor-not-allowed flex justify-center items-center"
          >
            {status === 'loading' ? (
              <span className="animate-spin h-5 w-5 border-2 border-white border-t-transparent rounded-full" />
            ) : (
              'Withdraw Now'
            )}
          </button>
        </div>
      </div>

      {/* Recent History Preview */}
      {history && history.length > 0 && (
        <div className="bg-gray-50 p-4 rounded-xl">
          <h4 className="text-sm font-bold text-gray-600 mb-2">Recent Activity</h4>
          <div className="text-xs text-gray-500">
            Last withdrawal: {history[0].created_at} ({history[0].status})
          </div>
        </div>
      )}
    </div>
  );
};

