import React, { useState, useEffect } from "react";
import { WithdrawWarningMessage } from "@orderly.network/ui-transfer";
import { useAccountInfo, useChainInfo } from "@orderly.network/hooks";
import { Alert } from "@orderly.network/ui";

export const AdvancedWithdrawWarning = () => {
  const { accountInfo } = useAccountInfo();
  const { chains } = useChainInfo();
  const [amount, setAmount] = useState(0);
  const [isBridgeless, setIsBridgeless] = useState(false);
  const [selectedChain, setSelectedChain] = useState("Arbitrum");

  // Simulate fetching fee estimate based on chain
  const estimatedFee = isBridgeless ? 0 : 2.5; 

  const handleChainChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedChain(e.target.value);
    // Reset bridgeless if chain doesn't support it (simulation)
    setIsBridgeless(false);
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-sm border border-gray-200">
      <div className="grid grid-cols-2 gap-4 mb-4">
        <div>
          <label className="text-xs text-gray-500">Destination Chain</label>
          <select 
            value={selectedChain} 
            onChange={handleChainChange}
            className="w-full mt-1 p-2 border rounded"
          >
            {chains?.map(c => <option key={c.id} value={c.name}>{c.name}</option>)}
          </select>
        </div>
        <div className="flex items-center pt-6">
          <label className="flex items-center space-x-2 cursor-pointer">
            <input 
              type="checkbox" 
              checked={isBridgeless} 
              onChange={() => setIsBridgeless(!isBridgeless)} 
              className="rounded"
            />
            <span className="text-sm">Enable Bridgeless</span>
          </label>
        </div>
      </div>

      <div className="mb-4">
        <label className="text-xs text-gray-500">Amount (USDC)</label>
        <input 
          type="number" 
          value={amount} 
          onChange={(e) => setAmount(Number(e.target.value))}
          className="w-full mt-1 p-2 border rounded"
        />
      </div>

      {/* Comprehensive Warning System */}
      <WithdrawWarningMessage 
        amount={amount} 
        token="USDC" 
        bridgeless={isBridgeless}
        fee={estimatedFee}
        totalBalance={accountInfo?.totalEquity}
        toChainId={chains?.find(c => c.name === selectedChain)?.id}
      />

      {amount > 0 && (
        <div className="mt-2 text-xs text-gray-400 text-right">
          Est. Fee: {estimatedFee} USDC
        </div>
      )}
    </div>
  );
};
