        },
        "userId": {
          "type": "string",
          "description": "User ID",
          "example": "user123"
        },
        "brokerId": {
          "type": "string",
          "description": "Broker ID",
          "example": "my-dex"
        },
        "brokerName": {
          "type": "string",
          "description": "Display name of the DEX",
          "example": "My DEX"
        },
        "repoUrl": {
          "type": "string",
          "nullable": true,
          "description": "GitHub repository URL",
          "example": "https://github.com/user/my-dex"
        },
        "customDomain": {
          "type": "string",
          "nullable": true,
          "description": "Custom domain for the DEX",
          "example": "dex.example.com"
        },
        "description": {
          "type": "string",
          "nullable": true,
          "description": "DEX description",
          "example": "A decentralized exchange"
        },
        "banner": {
          "type": "string",
          "nullable": true,
          "description": "Banner image URL",
          "example": "https://example.com/banner.png"
        },
        "logo": {
          "type": "string",
          "nullable": true,
          "description": "Logo image URL",
          "example": "https://example.com/logo.png"
        },
        "primaryLogo": {
          "type": "string",
          "nullable": true,
          "description": "Primary logo asset URL",
          "example": "https://example.com/primary-logo.png"
        },
        "secondaryLogo": {
          "type": "string",
          "nullable": true,
          "description": "Secondary logo asset URL",
          "example": "https://example.com/secondary-logo.png"
        },
        "favicon": {
          "type": "string",
          "nullable": true,
          "description": "Favicon URL",
          "example": "https://example.com/favicon.ico"
        },
        "tokenAddress": {
          "type": "string",
          "nullable": true,
          "description": "Token contract address",
          "example": "0x1234567890123456789012345678901234567890"
        },
        "tokenChain": {
          "type": "string",
          "nullable": true,
          "description": "Token blockchain network",
          "example": "ethereum"
        },
        "telegramLink": {
          "type": "string",
          "nullable": true,
          "description": "Telegram group link",
          "example": "https://t.me/mygroup"
        },
        "discordLink": {
          "type": "string",
          "nullable": true,
          "description": "Discord invite link",
          "example": "https://discord.gg/invite"
        },
        "xLink": {
          "type": "string",
          "nullable": true,
          "description": "X (Twitter) profile link",
          "example": "https://x.com/mydex"
        },
        "websiteUrl": {
          "type": "string",
          "nullable": true,
          "description": "Official website URL",
          "example": "https://mydex.com"
        },
        "isGraduated": {
          "type": "boolean",
          "description": "Whether the DEX has graduated from demo",
          "example": false
        },
        "showOnBoard": {
          "type": "boolean",
          "description": "Whether to show on public leaderboard",
          "example": true
        },
        "createdAt": {
          "type": "string",
          "format": "date-time",
          "description": "Creation timestamp",
          "example": "2024-01-01T00:00:00.000Z"
        },
        "updatedAt": {
          "type": "string",
          "format": "date-time",
          "description": "Last update timestamp",
          "example": "2024-01-01T00:00:00.000Z"
        }
      },
      "required": [
        "id",
        "userId",
        "brokerId",
        "brokerName",
        "repoUrl",
        "customDomain",
        "description",
        "banner",
        "logo",
        "primaryLogo",
        "secondaryLogo",
        "favicon",
        "tokenAddress",
        "tokenChain",
        "telegramLink",
        "discordLink",
        "xLink",
        "websiteUrl",
        "isGraduated",
        "showOnBoard",
        "createdAt",
        "updatedAt"
      ]
    }
  },
  "required": [
    "message",
    "dex"
  ]
