import { useApiKeyManager } from '@orderly.network/hooks';

export const ApiKeySettings = () => {
  // Destructure creation, deletion, and listing capabilities
  const { 
    createApiKey, 
    deleteApiKey, 
    apiKeys, 
    isLoading 
  } = useApiKeyManager();

  const handleCreate = async () => {
    try {
      // Typically requires a signature or wallet interaction
      const newKey = await createApiKey();
      console.log('API Key created:', newKey);
    } catch (error) {
      console.error('Failed to create key', error);
    }
  };

  return (
    <div>
      <h3>API Keys</h3>
      <button onClick={handleCreate} disabled={isLoading}>
        Generate New Key
      </button>
      <ul>
        {apiKeys?.map((key) => (
          <li key={key.key_id}>
            {key.key_id} 
            <button onClick={() => deleteApiKey(key.key_id)}>Revoke</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

import { useApiStatusActions } from '@orderly.network/hooks';

export const ConnectionManager = () => {
  // Access actions to control the API connection state
  const { connect, disconnect, checkStatus } = useApiStatusActions();

  return (
    <div className="flex gap-2">
      <button onClick={() => connect()}>
        Reconnect API
      </button>
      <button onClick={() => disconnect()}>
        Disconnect
      </button>
      <button onClick={() => checkStatus()}>
        Check Health
      </button>
    </div>
  );
};

import { useDataCenterContext } from '@orderly.network/hooks';

export const MarketInfo = ({ symbol }: { symbol: string }) => {
  const { getSymbolInfo } = useDataCenterContext();

  // Retrieve static market data for a specific trading pair
  const marketData = getSymbolInfo(symbol);

  if (!marketData) return <div>Market {symbol} not found</div>;

  return (
    <div className="grid grid-cols-2 gap-4">
      <div>
        <span className="text-gray-500 block">Min Order Size</span>
        <span className="font-mono">{marketData.min_order_size}</span>
      </div>
      <div>
        <span className="text-gray-500 block">Tick Size</span>
        <span className="font-mono">{marketData.tick_size}</span>
      </div>
      <div>
        <span className="text-gray-500 block">Quote Asset</span>
        <span className="font-mono">{marketData.quote_asset}</span>
      </div>
    </div>
  );
};

import { useCommission } from '@orderly.network/hooks';

export const ReferralStats = () => {
  // Fetches the commission volume generated by the user's referrals
  const { data, isLoading } = useCommission();

  return (
    <div className="card bg-white p-6 shadow rounded">
      <h2 className="text-xl font-semibold mb-4">Commission Volume</h2>

      {isLoading ? (
        <p>Loading stats...</p>
      ) : (
        <div className="text-3xl font-bold text-blue-600">
          {data?.volume ? `${data.volume} USDC` : '0 USDC'}
        </div>
      )}

      <p className="text-sm text-gray-500 mt-2">
        Total trading volume generated from your referred users.
      </p>
    </div>
  );
};

import { useRefereeInfo } from '@orderly.network/hooks';

export const RefereeList = () => {
  // Fetches a list of users who have registered using the user's referral code
  const { data: referees, isLoading } = useRefereeInfo();

  return (
    <div className="overflow-x-auto">
      <h3 className="text-lg font-medium mb-3">My Referees</h3>

      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <table className="min-w-full text-sm text-left">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-4 py-2">Address</th>
              <th className="px-4 py-2">Registered At</th>
              <th className="px-4 py-2">Status</th>
            </tr>
          </thead>
          <tbody>
            {referees && referees.length > 0 ? (
              referees.map((referee: any, index: number) => (
                <tr key={index} className="border-b">
                  <td className="px-4 py-2 font-mono">{referee.address}</td>
                  <td className="px-4 py-2">{new Date(referee.timestamp).toLocaleDateString()}</td>
                  <td className="px-4 py-2 text-green-500">Active</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={3} className="px-4 py-2 text-center text-gray-500">
                  No referees found yet.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

import { useAllBrokers } from '@orderly.network/hooks';

export const BrokerSelector = () => {
  // Fetches the list of all available brokers on the network
  const { data: brokers, isLoading, error } = useAllBrokers();

  if (isLoading) return <div>Loading brokers...</div>;
  if (error) return <div>Failed to load brokers.</div>;

  return (
    <select className="broker-select">
      <option value="">Select a Broker</option>
      {brokers?.map((broker) => (
        <option key={broker.broker_id} value={broker.broker_id}>
          {broker.name}
        </option>
      ))}
    </select>
  );
};

import { useCurEpochEstimate } from '@orderly.network/hooks';

export const RewardsEstimateCard = () => {
  // Provides estimated rewards/points for the current trading epoch
  const { data: estimate, isLoading } = useCurEpochEstimate();

  return (
    <div className="reward-card">
      <h3>Current Epoch Estimates</h3>
      {isLoading ? (
        <p>Calculating...</p>
      ) : (
        <div>
          <p>Est. Rewards: <strong>{estimate?.rewardAmount || 0} ORDER</strong></p>
