import { useCalculatorService } from '@orderly.network/hooks';

export const OrderPreview = ({ symbol, side, quantity, price }) => {
  // Accesses the calculator service for estimating costs and slippage
  const calculator = useCalculatorService();

  const estimatedTotal = calculator?.calculateTotalCost({
    symbol,
    side,
    quantity,
    price
  });

  return (
    <div className="order-preview">
      <h4>Order Estimation</h4>
      <p>Estimated Cost: {estimatedTotal?.toFixed(4) || '---'}</p>
    </div>
  );
};

import { useConfig } from '@orderly.network/hooks';

export const NetworkStatus = () => {
  // Retrieves global configuration settings (Chain ID, Broker ID, etc.)
  const config = useConfig();

  return (
    <div className="network-info">
      <p>Environment: {config?.env}</p>
      <p>Chain ID: {config?.chainId}</p>
      <p>Broker ID: {config?.brokerId}</p>
    </div>
  );
};

import { useDatabaseInitialization } from '@orderly.network/hooks';

export const AppInitializer = ({ children }) => {
  // Ensures the local IndexedDB is initialized before rendering the app
  const { isInitialized, error } = useDatabaseInitialization();

  if (error) {
    return <div>Database Error: {error.message}</div>;
  }

  if (!isInitialized) {
    return <div>Initializing local storage...</div>;
  }

  // App is ready to render components that rely on local DB
  return <>{children}</>;
};

import { useEffect } from 'react';
import { useEventEmitter } from '@orderly.network/hooks';

export const OrderNotifications = () => {
  const emitter = useEventEmitter();

  useEffect(() => {
    // Subscribe to order updates
    const handleOrderUpdate = (data) => {
      if (data.status === 'FILLED') {
        console.log('Order filled!', data);
        // Trigger toast notification logic here
      }
    };

    emitter.on('ORDER_UPDATE', handleOrderUpdate);

    // Cleanup subscription
    return () => {
      emitter.off('ORDER_UPDATE', handleOrderUpdate);
    };
  }, [emitter]);

  return null; // This component handles logic only
};

import { useFeeState } from '@orderly.network/hooks';

export const FeeDisplay = () => {
  // Fetches the current fee rates (Maker/Taker) for the connected account
  const { data: feeState, isLoading } = useFeeState();

  if (isLoading) return <div>Loading fees...</div>;

  return (
    <div className="fee-info">
      <h4>Your Fee Tier</h4>
      <p>Maker Fee: {feeState?.maker_fee_rate}%</p>
      <p>Taker Fee: {feeState?.taker_fee_rate}%</p>
      <p>Discount: {feeState?.discount || 0}%</p>
    </div>
  );
};

import { useKeyStore } from '@orderly.network/hooks';

export const WalletConnector = () => {
  const { keyStore, status } = useKeyStore();

  const handleSign = async () => {
    if (!keyStore) {
      console.log('Wallet not connected');
      return;
    }

    // Example of using the keyStore to sign a message
    // Note: The SDK handles most signing internally, but this exposes the capability
    try {
      const address = await keyStore.getAddress();
      console.log('Connected Address:', address);
    } catch (err) {
      console.error('Signing error', err);
    }
  };

  return (
    <div>
      <p>Status: {status}</p>
      <button onClick={handleSign}>Check Address</button>
    </div>
  );
};

import { useNetworkInfo } from '@orderly.network/hooks';

export const NetworkStatus = () => {
  // Returns the current blockchain network ID and detailed info
  const { id, info } = useNetworkInfo();

  return (
    <div className="network-badge">
      <span>Network ID: {id}</span>
      {info && (
        <span className="text-xs text-gray-500">
          ({info.name})
        </span>
      )}
    </div>
  );
};

import { useParamsCheck } from '@orderly.network/hooks';
import { useState } from 'react';

export const TradeValidator = () => {
  // Hook to validate parameters before sending to the API
  const checkParams = useParamsCheck();
  const [isValid, setIsValid] = useState(true);

  const handleTradeCheck = (symbol: string, amount: number) => {
    // Example validation logic using the hook
    const result = checkParams.validate({ symbol, amount });

    if (!result.valid) {
      setIsValid(false);
      console.error('Validation failed:', result.errors);
    } else {
      setIsValid(true);
    }
  };

  return (
    <button 
      disabled={!isValid}
      onClick={() => handleTradeCheck('PERP_ETH_USDC', 0.1)}
    >
      {isValid ? 'Execute Trade' : 'Invalid Params'}
    </button>
  );
};

import { usePoster } from '@orderly.network/hooks';
import { useEffect, useState } from 'react';

export const OrderSubmission = () => {
  // Hook for making authenticated POST requests to Orderly
  const poster = usePoster();
  const [loading, setLoading] = useState(false);

  const placeOrder = async () => {
    setLoading(true);
    try {
      const response = await poster('/v1/order', {
        symbol: 'PERP_BTC_USDC',
        side: 'BUY',
        order_type: 'MARKET',
        quantity: '0.01'
      });
      console.log('Order placed:', response);
    } catch (error) {
      console.error('Order failed:', error);
    } finally {
      setLoading(false);
    }
  };

  return <button onClick={placeOrder}>{loading ? 'Sending...' : 'Buy BTC'}</button>;
};

import { usePreLoadData } from '@orderly.network/hooks';

export const AppLoader = ({ children }: { children: React.ReactNode }) => {
  // Returns loading state and error status for initial data fetch
  const { error, done } = usePreLoadData();

  if (error) {
    return (
      <div className="error-screen">
        <p>Failed to load account data.</p>
        <button onClick={() => window.location.reload()}>Retry</button>
      </div>
    );
  }

  if (!done) {
    return <div className="spinner">Syncing with Orderly...</div>;
  }

  // Only render the main app when data is fully loaded
  return <>{children}</>;
};

import { useRestrictedInfo } from '@orderly.network/hooks';

export const RegionGuard = () => {
  // Checks if the user's IP region is restricted
  const { invalidRegions } = useRestrictedInfo();

  if (invalidRegions) {
    return (
      <div className="p-4 bg-red-100 text-red-700">
        <h2>Service Unavailable</h2>
        <p>Trading is not available in your current region due to regulatory restrictions.</p>
      </div>
    );
  }

  return (
    <div className="trading-interface">
      {/* Main trading UI components */}
      <h1>Trade Now</h1>
    </div>
  );
};

import { useTrack } from '@orderly.network/hooks';

export const TradeButton = () => {
  // Returns a tracking function to log user events
  const track = useTrack();

  const handleClick = () => {
    // Log an event for analytics
    track('button_click', {
      element: 'buy_button',
      page: 'trade_view',
      symbol: 'PERP_ETH_USDC'
    });

    // Execute trade logic...
  };

  return <button onClick={handleClick}>Buy ETH</button>;
};

import { useTrackingInstance } from '@orderly.network/hooks';
import { useEffect } from 'react';

export const CustomAnalytics = () => {
  // Access the raw tracking provider instance
  const tracker = useTrackingInstance();

  useEffect(() => {
    if (tracker) {
      // Directly calling a method on the underlying analytics library
      // e.g., setting a user ID or specific page view
      tracker.setUserId('user_12345');
      tracker.page('Trading Dashboard');
    }
  }, [tracker]);

  return null;
};

import { useOrderEntry } from '@orderly.network/hooks';

/**
 * NOTE: This hook is marked as deprecated in the source path.
 * Consider using useOrderEntryFromStore or useOrderStore for new implementations.
 */
export const LegacyOrderForm = () => {
  const { placeOrder, reduceOrder, isPlacing } = useOrderEntry();

  const handleMarketBuy = async () => {
    try {
      await placeOrder({
        symbol: 'PERP_ETH_USDC',
        side: 'BUY',
        order_type: 'MARKET',
        order_quantity: '0.1',
      });
    } catch (err) {
      console.error('Order failed', err);
    }
  };

  return (
    <button onClick={handleMarketBuy} disabled={isPlacing}>
