import { useOrderEntryFromStore } from '@orderly.network/hooks';

export const OrderInputDisplay = () => {
  // Accesses the global Zustand store for order entry state
  const { 
    price, 
    quantity, 
    side, 
    symbol,
    setPrice, 
    setQuantity 
  } = useOrderEntryFromStore();

  return (
    <div className="p-4 border rounded">
      <h2>Place Order ({symbol})</h2>
      <div>Current Side: {side}</div>

      <label>Price:</label>
      <input 
        type="number" 
        value={price} 
        onChange={(e) => setPrice(e.target.value)} 
      />

      <label>Quantity:</label>
      <input 
        type="number" 
        value={quantity} 
        onChange={(e) => setQuantity(e.target.value)} 
      />
    </div>
  );
};

import { useOrderEntity } from '@orderly.network/hooks';

export const OrderConstraints = () => {
  // Returns specific metadata about the trading pair
  const { maxQty, symbol } = useOrderEntity();

  return (
    <div className="text-xs text-gray-500">
      <p>Symbol: {symbol}</p>
      <p>Max Order Quantity: {maxQty}</p>
    </div>
  );
};

import { useOrderStore } from '@orderly.network/hooks';

export const OrderActionsPanel = () => {
  // Accesses actions to manipulate the global order state
  const { actions } = useOrderStore();

  const resetForm = () => {
    actions.reset();
  };

  const setMarketOrder = () => {
    actions.setOrderType('MARKET');
  };

  return (
    <div className="flex gap-4">
      <button onClick={resetForm}>Reset Form</button>
      <button onClick={setMarketOrder}>Switch to Market</button>
    </div>
  );
};

import { useMarketTradeStream } from '@orderly.network/hooks';

export const RecentTrades = ({ symbol }: { symbol: string }) => {
  // Subscribes to the websocket trade feed for a specific market.
  // Returns an array of trade objects (data) representing recent fills.
  const { data } = useMarketTradeStream({ symbol });

  return (
    <div className="trade-history">
      <h4>Recent Trades</h4>
      <table>
        <thead>
          <tr>
            <th>Price</th>
            <th>Size</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {data?.map((trade, index) => (
            <tr key={`${trade.trade_id}-${index}`}>
              <td>{trade.price}</td>
              <td>{trade.quantity}</td>
              <td>{new Date(trade.timestamp).toLocaleTimeString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

import { useOrderStream } from '@orderly.network/hooks';

export const OpenOrdersTable = () => {
  // Subscribes to the user's private order stream via WebSocket.
  // Returns real-time updates for orders (New, Filled, Partially Filled, Canceled).
  const { data: orders } = useOrderStream();

  return (
    <div className="orders-panel">
      <h3>Your Orders</h3>
      {orders?.length === 0 ? (
        <p>No open orders</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Symbol</th>
              <th>Side</th>
              <th>Size</th>
              <th>Price</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.order_id}>
                <td>{order.symbol}</td>
                <td className={order.side === 'BUY' ? 'text-green' : 'text-red'}>
                  {order.side}
                </td>
                <td>{order.quantity}</td>
                <td>{order.price}</td>
                <td>{order.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

import { useOrderbookStream } from '@orderly.network/hooks';

export const OrderBook = ({ symbol }: { symbol: string }) => {
  // Subscribes to the websocket for the specific symbol's orderbook
  const { asks, bids } = useOrderbookStream(symbol);

  return (
    <div className="order-book">
      <h3>Order Book: {symbol}</h3>
      <div className="asks">
        <h4>Asks (Sell)</h4>
        {asks.slice(0, 5).map((ask, i) => (
          <div key={i} className="row">
            <span>{ask[0]}</span> {/* Price */}
            <span>{ask[1]}</span> {/* Size */}
          </div>
        ))}
      </div>
      <div className="bids">
        <h4>Bids (Buy)</h4>
        {bids.slice(0, 5).map((bid, i) => (
          <div key={i} className="row">
            <span>{bid[0]}</span> {/* Price */}
            <span>{bid[1]}</span> {/* Size */}
          </div>
        ))}
      </div>
    </div>
  );
};

import { useOrderlyContext } from '@orderly.network/hooks';
import { Button } from './ui';

export const ConnectionStatus = () => {
  const { state, address, disconnect } = useOrderlyContext();

  return (
    <div className="flex items-center justify-between p-4 bg-gray-100 rounded">
      <div>
        <p className="text-sm text-gray-500">Status</p>
        <p className={`font-bold ${state === 'connected' ? 'text-green-600' : 'text-yellow-600'}`}>
          {state.toUpperCase()}
        </p>
        {address && <p className="text-xs font-mono mt-1">{address}</p>}
      </div>

      {state === 'connected' && (
        <Button onClick={disconnect} variant="destructive">
          Disconnect Wallet
        </Button>
      )}
    </div>
  );
};

import { usePositionClose } from '@orderly.network/hooks';

export const ClosePositionButton = ({ symbol, size }) => {
  const { closePosition, isClosing } = usePositionClose();

  const handleClose = async () => {
    try {
      // Executes a market order to reduce the position to 0
      await closePosition({ 
        symbol: symbol,
        // Usually calculates the opposite side automatically based on current position
      });
    } catch (error) {
      console.error('Failed to close position', error);
    }
  };

  return (
    <button 
      onClick={handleClose} 
      disabled={isClosing}
      className="bg-red-500 text-white px-4 py-2 rounded"
    >
      {isClosing ? 'Closing...' : 'Close Position'}
    </button>
  );
};

import { usePositionStream } from '@orderly.network/hooks';

export const AccountSummary = () => {
  // Streams real-time position updates, calculating PnL on the fly
  const { unrealPnL, total_unreal_pnl, unrealPnlROI } = usePositionStream();

  return (
    <div className="account-stats">
      <div className="stat-card">
        <span>Total Unrealized PnL</span>
        <span className={total_unreal_pnl >= 0 ? 'profit' : 'loss'}>
          {total_unreal_pnl.toFixed(2)} USDC
        </span>
      </div>
      <div className="stat-card">
        <span>ROI</span>
        <span>{unrealPnlROI.toFixed(2)}%</span>
      </div>
    </div>
  );
};

import { useAccountInfo } from '@orderly.network/hooks';

export const AccountBalance = () => {
  // Fetches comprehensive account data including balance and equity
  const { accountInfo, isLoading } = useAccountInfo();

  if (isLoading) return <div>Loading balance...</div>;

  return (
    <div className="account-summary">
      <h3>Account Overview</h3>
      <p>Total Equity: ${accountInfo?.equity}</p>
      <p>Available Balance: ${accountInfo?.availableBalance}</p>
      <p>Margin Ratio: {accountInfo?.marginRatio}%</p>
    </div>
  );
};

import { useSubAccountAlgoOrderStream } from '@orderly.network/hooks';

export const AlgoOrdersPanel = ({ accountId }: { accountId: number }) => {
  // Subscribes to algorithmic orders (Take Profit, Stop Loss, TWAP) for a specific sub-account.
  // Returns a list of active conditional orders.
  const { data: algoOrders } = useSubAccountAlgoOrderStream({ accountId });

  return (
    <div className="algo-orders">
      <h4>Conditional Orders (Account {accountId})</h4>
      <ul>
        {algoOrders?.map((algo) => (
          <li key={algo.algo_order_id}>
            <span>{algo.type}: {algo.symbol}</span>
            <span>Trigger: {algo.trigger_price}</span>
            <span>Status: {algo.status}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

import { useWalletSubscription } from "@orderly.network/hooks";
import { useEffect, useState } from "react";

export const WalletSummary = () => {
  const [balance, setBalance] = useState("0");

  // Subscribes to wallet updates via WebSocket
  useWalletSubscription((data) => {
    // Callback triggered on balance change
    console.log("Wallet update:", data);
    // Update local state with new total equity or available balance
    if (data.account) {
      setBalance(data.account.total_equity);
    }
  });

  return (
    <div className="wallet-summary">
      <h3>Total Equity: ${balance}</h3>
    </div>
  );
};

import { useWalletTopic } from '@orderly.network/hooks';

export const WalletBalance = () => {
  // Subscribes to the wallet websocket topic for real-time balance updates
  const { data, error, isConnected } = useWalletTopic();

  if (error) return <div>Error loading wallet data</div>;
  if (!isConnected) return <div>Connecting to wallet feed...</div>;

  return (
    <div className="p-4 border rounded">
      <h3 className="font-bold mb-2">Real-time Wallet Overview</h3>
      {/* Data structure typically contains total_equity, available_balance, etc. */}
      <p>Equity: {data?.holding?.total_equity || 'Loading...'}</p>
      <p>Available: {data?.holding?.available_balance || 'Loading...'}</p>
    </div>
  );
};

import { useSubAccountDataObserver } from '@orderly.network/hooks';

export const SubAccountHoldings = ({ accountId }: { accountId: number }) => {
  // Observes the holdings (balances) of a specific sub-account
  const { holding, isLoading } = useSubAccountDataObserver(accountId);

  if (isLoading) return <p>Loading sub-account data...</p>;

  return (
    <div className="border p-4 rounded bg-gray-50">
      <h4 className="font-bold mb-2">Sub-Account #{accountId} Holdings</h4>

      {holding && holding.length > 0 ? (
        <ul className="space-y-2">
          {holding.map((asset: any) => (
            <li key={asset.token} className="flex justify-between">
              <span className="uppercase font-medium">{asset.token}</span>
              <span className="font-mono">
                {parseFloat(asset.total_balance).toFixed(4)}
              </span>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-500">No assets found in this sub-account.</p>
      )}
    </div>
  );
};

import { useSubAccountMaxWithdrawal } from '@orderly.network/hooks';
import { useState } from 'react';

export const SubAccountWithdraw = ({ accountId, token }: { accountId: number, token: string }) => {
  const [amount, setAmount] = useState('');

  // Calculates the maximum amount that can be withdrawn from a sub-account for a specific token
  const { data: maxWithdrawal, isLoading } = useSubAccountMaxWithdrawal(accountId, token);

  const handleMaxClick = () => {
    if (maxWithdrawal) {
      setAmount(maxWithdrawal.max_withdrawal);
    }
  };

  return (
    <div className="flex flex-col gap-2">
      <label className="text-sm font-medium">Withdraw Amount ({token})</label>
      <div className="relative">
        <input 
          type="number" 
          value={amount} 
          onChange={(e) => setAmount(e.target.value)}
          className="w-full border rounded p-2 pr-16"
          placeholder="0.00"
        />
        <button 
          onClick={handleMaxClick}
          disabled={isLoading}
          className="absolute right-2 top-2 text-xs bg-blue-100 text-blue-600 px-2 py-1 rounded hover:bg-blue-200"
        >
          MAX
        </button>
      </div>
      {isLoading && <p className="text-xs text-gray-500">Calculating limits...</p>}
    </div>
  );
};

import { useSubAccountWS } from '@orderly.network/hooks';
