import { useAccount } from '@orderly.network/hooks';

export const AccountManager = () => {
  const {
    isMainAccount,
    subAccount,
    refresh,
    create,
    update
  } = useAccount();

  const handleCreateSubAccount = async () => {
    try {
      await create();
      await refresh(); // Refresh state after creation
    } catch (err) {
      console.error('Failed to create sub-account', err);
    }
  };

  return (
    <div className="account-panel">
      <div className="status-badge">
        {isMainAccount ? 'Main Account' : 'Sub-Account'}
      </div>

      {isMainAccount && (
        <button onClick={handleCreateSubAccount}>
          Create Sub-Account
        </button>
      )}

      {subAccount && (
        <div className="sub-account-info">
          <p>Address: {subAccount.address}</p>
        </div>
      )}
    </div>
  );
};

