import { useComputedLTV } from '@orderly.network/hooks';

export const MarginHealth = ({ symbol, quantity }: { symbol: string; quantity: number }) => {
  // Calculates Loan-to-Value components based on current oracle prices and risk weights
  const { qty, indexPrice, weight } = useComputedLTV(symbol, quantity);

  if (!indexPrice) return <div>Loading price data...</div>;

  const notionalValue = qty * indexPrice;
  const weightedValue = notionalValue * weight;

  return (
    <div className="p-4 border rounded">
      <h4>Margin Analysis for {symbol}</h4>
      <p>Index Price: ${indexPrice.toFixed(2)}</p>
      <p>Risk Weight: {(weight * 100).toFixed(2)}%</p>
      <p>Weighted Collateral Value: ${weightedValue.toFixed(2)}</p>
    </div>
  );
};

