import React, { useState } from 'react';
import { useInternalTransfer } from '@orderly.network/hooks';

export const TransferFunds = () => {
  const [amount, setAmount] = useState('');
  // Hook to handle transfers between Orderly Account and Broker Wallet
  const { transfer, isLoading } = useInternalTransfer();

  const handleTransfer = async () => {
    try {
      // Assuming token and direction are handled or defaults are set
      await transfer({
        token: 'USDC',
        amount: parseFloat(amount),
        // brokerId: 'BROKER_ID' // May be required depending on context
      });
      alert('Transfer successful!');
    } catch (e) {
      console.error(e);
    }
  };

  return (
    <div>
      <input 
        type="number" 
        value={amount} 
        onChange={(e) => setAmount(e.target.value)} 
        placeholder="Amount"
      />
      <button 
        onClick={handleTransfer} 
        disabled={isLoading || !amount}
      >
        {isLoading ? 'Transferring...' : 'Transfer'}
      </button>
    </div>
  );
};

