export const LeverageControl = ({ symbol }: { symbol: string }) => {
  // Returns loading state and leverage data/mutators
  const { isLoading, leverage, setLeverage, maxLeverage } = useSymbolLeverage(symbol);

  const handleLeverageChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseInt(e.target.value);
    if (val > 0 && val <= maxLeverage) {
      await setLeverage(val);
    }
  };

  if (isLoading) return <div>Loading leverage settings...</div>;

  return (
    <div className="leverage-control">
      <label>Current Leverage: {leverage}x</label>
      <input 
        type="range" 
        min="1" 
        max={maxLeverage} 
        value={leverage} 
        onChange={handleLeverageChange} 
      />
    </div>
  );
};

