import { useEffect } from 'react';

export const OrderBookFeed = () => {
  // Hook to manage WebSocket connections and subscriptions
  const { on, send, ws } = useWS();

  useEffect(() => {
    // Subscribe to a topic
    send({
      id: 1,
      event: 'subscribe',
      topic: 'orderbook',
      params: { symbol: 'PERP_ETH_USDC' }
    });

    // Listen for messages
    const unsubscribe = on('message', (data) => {
      if (data.topic === 'orderbook') {
        console.log('Orderbook update:', data.data);
      }
    });

    return () => {
      // Cleanup on unmount
      unsubscribe();
    };
  }, [on, send]);

  return <div>Listening to OrderBook...</div>;
};

