import { useApiKeyManager } from '@orderly.network/hooks';

export const ApiKeySettings = () => {
  // Destructure the manager to handle creation and listing of keys
  const { 
    createApiKey, 
    getApiKeys, 
    deleteApiKey, 
    isLoading, 
    apiKeys 
  } = useApiKeyManager();

  const handleCreateNewKey = async () => {
    try {
      // Create a key with specific permissions
      await createApiKey({
        label: 'Trading Bot Key',
        // Enable specific scopes based on your app's needs
        scopes: ['read', 'trade'] 
      });
      // Refresh the list after creation
      await getApiKeys();
    } catch (error) {
      console.error('Failed to create API Key', error);
    }
  };

  return (
    <div className="p-4 border rounded">
      <h3>API Key Management</h3>
      <button 
        onClick={handleCreateNewKey}
        disabled={isLoading}
        className="px-4 py-2 bg-blue-500 text-white rounded"
      >
        {isLoading ? 'Generating...' : 'Generate New Key'}
      </button>

      <ul className="mt-4">
        {apiKeys?.map((key) => (
          <li key={key.key_id} className="flex justify-between items-center">
            <span>{key.label}</span>
            <button 
              onClick={() => deleteApiKey(key.key_id)}
              className="text-red-500"
            >
              Revoke
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

import { useApiStatusActions } from '@orderly.network/hooks';

export const ConnectionStatus = () => {
  // Access actions to manipulate the global API status state
  const { checkHealth, setMaintenanceMode, isConnected } = useApiStatusActions();

  return (
    <div className="flex items-center gap-4 p-4 bg-gray-100 rounded">
      <div className="flex items-center gap-2">
        <span className={`w-3 h-3 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`} />
        <span>API Status: {isConnected ? 'Connected' : 'Disconnected'}</span>
      </div>

      <button 
        onClick={checkHealth}
        className="px-3 py-1 text-sm border rounded hover:bg-gray-50"
      >
        Re-check Health
      </button>

      {/* Example of manually toggling maintenance mode for testing */}
      <button 
        onClick={() => setMaintenanceMode(true)}
        className="px-3 py-1 text-sm text-red-600 border border-red-200 rounded"
      >
        Simulate Maintenance
      </button>
    </div>
  );
};

import { useDataCenterContext } from '@orderly.network/hooks';

export const MarketInfo = ({ symbol }: { symbol: string }) => {
  // Access static or semi-static market data (symbols, configs, tickers)
  const { getSymbolInfo } = useDataCenterContext();

  const marketData = getSymbolInfo(symbol);

  if (!marketData) return <div>Market {symbol} not found</div>;

  return (
    <div className="flex gap-4">
      <span>Symbol: {marketData.symbol}</span>
      <span>Min Size: {marketData.min_size}</span>
      <span>Tick Size: {marketData.tick_size}</span>
    </div>
  );
};

