import { useAllBrokers } from '@orderly.network/hooks';

export const BrokerSelector = () => {
  // Fetches the list of all available brokers on the network
  const { data: brokers, isLoading, error } = useAllBrokers();

  if (isLoading) return <div>Loading brokers...</div>;
  if (error) return <div>Failed to load brokers.</div>;

  return (
    <select className="broker-select">
      <option value="">Select a Broker</option>
      {brokers?.map((broker) => (
        <option key={broker.broker_id} value={broker.broker_id}>
          {broker.name}
        </option>
      ))}
    </select>
  );
};

