import { useCalculatorService } from '@orderly.network/hooks';

export const TradePreview = ({ symbol, side, quantity, price }) => {
  // Accesses the calculator service to estimate costs, impact, and fees
  const calculator = useCalculatorService();

  const calculateTotal = () => {
    if (!calculator || !quantity || !price) return 0;

    // Example calculation logic provided by the service
    return calculator.calculateTotalCost(symbol, quantity, price);
  };

  return (
    <div className="trade-preview">
      <p>Estimated Cost: ${calculateTotal().toFixed(2)}</p>
    </div>
  );
};

