import { useDatabaseInitialization } from '@orderly.network/hooks';

export const AppWrapper = ({ children }) => {
  // Ensures the local IndexedDB is initialized before rendering app features
  const { isInitialized, error } = useDatabaseInitialization();

  if (error) {
    return <div>Database Error. Please clear storage.</div>;
  }

  if (!isInitialized) {
    return <div>Initializing local storage...</div>;
  }

  // Only render children when DB is ready
  return <>{children}</>;
};

import { useEffect } from 'react';
import { useEventEmitter } from '@orderly.network/hooks';
import { toast } from 'react-toastify';

export const NotificationListener = () => {
  const emitter = useEventEmitter();

  useEffect(() => {
    if (!emitter) return;

    // Listen for specific SDK events
    const handleOrderFill = (data) => {
      toast.success(`Order filled: ${data.quantity} @ ${data.price}`);
    };

    emitter.on('ORDER_FILLED', handleOrderFill);

    // Cleanup listener on unmount
    return () => {
      emitter.off('ORDER_FILLED', handleOrderFill);
    };
  }, [emitter]);

  return null; // This component is logic-only
};

