import { useParamsCheck } from '@orderly.network/hooks';

export const OrderValidation = () => {
  // Hypothetical order parameters
  const orderParams = {
    symbol: "PERP_ETH_USDC",
    side: "BUY",
    order_type: "LIMIT",
    price: "2500.00",
    quantity: "0.1"
  };

  // Check if parameters are valid before sending to API
  const checkResult = useParamsCheck(orderParams);

  const handleSubmit = () => {
    if (checkResult.valid) {
      console.log('Parameters are valid, proceeding...');
    } else {
      console.error('Validation failed:', checkResult.message);
    }
  };

  return (
    <button onClick={handleSubmit}>
      Place Order
    </button>
  );
};

import { usePoster } from '@orderly.network/hooks';

export const OrderSubmitter = () => {
  // usePoster provides a function to send authenticated POST requests
  const poster = usePoster();

  const createOrder = async () => {
    try {
      const response = await poster('/v1/order', {
        symbol: 'PERP_BTC_USDC',
        side: 'BUY',
        order_type: 'MARKET',
        quantity: '0.01'
      });

      console.log('Order submitted successfully:', response.data);
    } catch (error) {
      console.error('Order submission failed:', error);
    }
  };

  return <button onClick={createOrder}>Execute Trade</button>;
};

import { usePreLoadData } from '@orderly.network/hooks';

export const AppInitializer = ({ children }) => {
  const { error, done } = usePreLoadData();

  if (error) {
    return (
      <div className="error-screen">
        <h2>Initialization Failed</h2>
        <p>Please refresh the page or check your connection.</p>
      </div>
    );
  }

  if (!done) {
    return (
      <div className="loading-screen">
        <p>Setting up Orderly Network...</p>
      </div>
    );
  }

  // Render the main app only after data is preloaded
  return <main>{children}</main>;
};

import { useRestrictedInfo } from '@orderly.network/hooks';

export const RegionGuard = () => {
