import { useTrackingInstance } from '@orderly.network/hooks';

export const CustomAnalytics = () => {
  // Access the raw tracking instance (e.g., Segment, Analytics.js)
  const trackingInstance = useTrackingInstance();

  const identifyUser = (userId) => {
    if (trackingInstance) {
      // Directly calling methods on the underlying analytics library
      trackingInstance.identify(userId, {
        plan: 'premium',
        platform: 'web'
      });
    }
  };

  return <button onClick={() => identifyUser('user_123')}>Identify User</button>;
};

import { useOrderEntry } from '@orderly.network/hooks';

/**
 * Note: This hook is marked as deprecated in the source path.
 * Consider using useOrderEntryFromStore or useOrderStore for new implementations.
 * This example demonstrates legacy usage.
 */
export const LegacyOrderForm = () => {
  const { 
    submitOrder, 
    orderState, 
    updateOrderState 
  } = useOrderEntry();

  const handleSubmit = async () => {
    // Basic validation before submission
    if (!orderState.symbol || !orderState.amount) return;

    try {
      await submitOrder({
        symbol: orderState.symbol,
        side: orderState.side, // 'BUY' or 'SELL'
        order_type: orderState.orderType, // 'MARKET', 'LIMIT'
        order_quantity: orderState.amount,
        // price is required for LIMIT orders
        order_price: orderState.orderType === 'LIMIT' ? orderState.price : undefined 
      });
    } catch (err) {
      console.error('Order submission failed', err);
    }
  };

  return (
    <div className="flex flex-col gap-2">
      <input 
        placeholder="Amount" 
        onChange={(e) => updateOrderState({ amount: e.target.value })} 
      />
      <button onClick={handleSubmit}>Place Order</button>
    </div>
  );
};

import { useOrderEntryFromStore } from '@orderly.network/hooks';

/**
 * This hook reads directly from the Zustand store managing order entry state.
 * It is useful for components that need to display the current form state
 * without necessarily modifying it.
 */
export const OrderSummaryDisplay = () => {
  // Select specific slices of state from the order entry store
  const { 
    symbol, 
    price, 
    amount, 
    side, 
    orderType 
  } = useOrderEntryFromStore();

  const total = price && amount ? (parseFloat(price) * parseFloat(amount)).toFixed(2) : '0.00';

  return (
    <div className="p-4 bg-gray-50 rounded border border-gray-200">
      <h4 className="font-bold mb-2">Order Summary</h4>
      <div className="grid grid-cols-2 gap-2 text-sm">
        <span>Symbol:</span> <span className="text-right font-mono">{symbol || '-'}</span>

        <span>Side:</span> 
        <span className={`text-right font-bold ${side === 'BUY' ? 'text-green-600' : 'text-red-600'}`}>
          {side || '-'}
        </span>

        <span>Type:</span> <span className="text-right">{orderType || '-'}</span>
        <span>Price:</span> <span className="text-right font-mono">{price || '-'}</span>
        <span>Amount:</span> <span className="text-right font-mono">{amount || '-'}</span>

        <span className="font-bold border-t pt-1">Total:</span> 
        <span className="text-right font-bold border-t pt-1 font-mono">{total}</span>
      </div>
    </div>
  );
};

import { useOrderEntity } from '@orderly.network/hooks';

export const QuantityInput = () => {
  // Returns specific metadata about the current trading context
  const { maxQty, symbol } = useOrderEntity();

  return (
    <div className="flex flex-col gap-1">
      <label className="text-xs text-gray-500 flex justify-between">
        <span>Quantity ({symbol})</span>
        <span>Max: {maxQty}</span>
      </label>

      <input
        type="number"
        // Use maxQty to prevent user from entering impossible order sizes
        max={maxQty}
        step="0.01"
        className="border p-2 rounded"
        placeholder="0.00"
      />

      <p className="text-[10px] text-gray-400">
        Order size cannot exceed {maxQty} for this asset.
      </p>
    </div>
  );
};

import { useOrderStore } from '@orderly.network/hooks';

/**
 * This hook provides access to the ACTIONS of the order store.
 * Use it to dispatch updates to the order form from UI components.
 */
export const OrderControls = () => {
  // Destructure actions to update the global order state
  const { 
    setSymbol, 
    setSide, 
    setPrice, 
    setAmount, 
    resetOrder 
  } = useOrderStore();

  return (
    <div className="flex gap-2">
      <select 
        onChange={(e) => setSymbol(e.target.value)}
        className="border p-2 rounded"
      >
        <option value="PERP_ETH_USDC">ETH-PERP</option>
        <option value="PERP_BTC_USDC">BTC-PERP</option>
      </select>

      <div className="flex bg-gray-200 rounded p-1">
        <button 
          onClick={() => setSide('BUY')}
          className="px-4 py-1 rounded bg-green-500 text-white"
        >
          Buy
        </button>
        <button 
          onClick={() => setSide('SELL')}
          className="px-4 py-1 rounded bg-red-500 text-white"
        >
          Sell
        </button>
      </div>

      <button 
        onClick={resetOrder}
        className="px-4 py-2 text-gray-600 underline"
      >
        Reset Form
      </button>
    </div>
  );
};

import { useMarketTradeStream } from '@orderly.network/hooks';

export const RecentTrades = ({ symbol }: { symbol: string }) => {
  // Subscribes to the public trade stream for the given symbol
  const { data: trades } = useMarketTradeStream(symbol);

  return (
    <div className="trade-history">
      <h4>Recent Trades</h4>
      <table>
        <thead>
          <tr>
            <th>Price</th>
            <th>Size</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {trades?.slice(0, 10).map((trade, index) => (
            <tr key={index}>
              <td>{trade.price}</td>
              <td>{trade.quantity}</td>
              <td>{new Date(trade.timestamp).toLocaleTimeString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

import { useOrderStream } from '@orderly.network/hooks';

export const OpenOrders = () => {
  // Subscribes to the user's private order updates via WebSocket
  const { data: orders } = useOrderStream();

  return (
    <div className="order-panel">
      <h3>Your Orders</h3>
      {orders && orders.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Symbol</th>
              <th>Side</th>
              <th>Size</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.order_id}>
                <td>{order.symbol}</td>
                <td className={order.side === 'BUY' ? 'text-green' : 'text-red'}>
                  {order.side}
                </td>
                <td>{order.quantity}</td>
                <td>{order.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No open orders.</p>
      )}
    </div>
  );
};

import { useOrderbookStream } from '@orderly.network/hooks';

