import { usePositionClose } from '@orderly.network/hooks';

export const PositionRow = ({ symbol, size, entryPrice }) => {
  const { closePosition, isClosing } = usePositionClose();

  const handleClose = async () => {
    if (!window.confirm(`Are you sure you want to close your ${symbol} position?`)) {
      return;
    }

    try {
      // Execute a market order to reduce the position to 0
      await closePosition({ 
        symbol: symbol,
        // Usually implies reducing position to zero
        size: size 
      });
    } catch (error) {
      console.error('Failed to close position', error);
    }
  };

  return (
    <tr className="border-b">
      <td className="p-2">{symbol}</td>
      <td className="p-2">{size}</td>
      <td className="p-2">{entryPrice}</td>
      <td className="p-2 text-right">
        <button 
          onClick={handleClose}
          disabled={isClosing}
          className={`px-3 py-1 rounded text-sm ${
            isClosing ? 'bg-gray-300' : 'bg-red-500 text-white hover:bg-red-600'
          }`}
        >
          {isClosing ? 'Closing...' : 'Close'}
        </button>
      </td>
    </tr>
  );
};

import { usePositionStream } from '@orderly.network/hooks';

