import { useSubAccountAlgoOrderStream } from '@orderly.network/hooks';

export const AlgoOrdersPanel = ({ accountId }: { accountId: number }) => {
  // Subscribes to algo orders (TP/SL, Stop Market) for a specific sub-account
  const { data: algoOrders } = useSubAccountAlgoOrderStream(accountId);

  return (
    <div className="algo-orders">
      <h3>Conditional Orders (Account {accountId})</h3>
      <ul>
        {algoOrders?.map((order) => (
          <li key={order.algo_order_id} className="mb-2 border-b pb-2">
            <div className="flex justify-between">
              <span>{order.symbol}</span>
              <span className={order.side === 'BUY' ? 'text-green-500' : 'text-red-500'}>
                {order.side}
              </span>
            </div>
            <div className="text-xs text-gray-500">
              Trigger: {order.trigger_price} | Type: {order.type}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

import { useWalletSubscription } from '@orderly.network/hooks';

export const WalletSummary = () => {
  // Subscribes to real-time updates for the wallet's balance and portfolio state
  const { data, status } = useWalletSubscription();

  if (status === 'connecting') return <div>Connecting to wallet...</div>;

  return (
    <div className="wallet-summary">
      <h3>Portfolio Value</h3>
      <p className="total-equity">
        ${data?.total_equity ? parseFloat(data.total_equity).toFixed(2) : '0.00'}
      </p>
      <div className="details">
        <p>Available: ${parseFloat(data?.available_balance || 0).toFixed(2)}</p>
        <p>Margin: ${parseFloat(data?.holding_balance || 0).toFixed(2)}</p>
      </div>
    </div>
  );
};

import { useWalletTopic } from '@orderly.network/hooks';

export const WalletBalance = () => {
  // Subscribes to the private wallet channel for real-time balance updates.
  // Returns the current wallet state/data payload.
  const { data, error, isConnected } = useWalletTopic();

  if (!isConnected) return <div>Connecting to wallet feed...</div>;
  if (error) return <div>Error loading wallet data</div>;

  return (
    <div className="p-4 border rounded">
      <h3 className="font-bold mb-2">Real-time Wallet State</h3>
      {/* Data structure depends on the specific Orderly API version, usually contains holdings */}
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  );
};

import { useSubAccountDataObserver } from '@orderly.network/hooks';

export const SubAccountHoldings = ({ subAccountId }: { subAccountId: number }) => {
  // Observes real-time holding data for a specific sub-account.
  // Returns 'holding' data structure.
  const { holding, isLoading } = useSubAccountDataObserver(subAccountId);

  if (isLoading) return <div>Loading holdings...</div>;

  return (
    <div className="bg-gray-100 p-3 rounded">
      <h4>Sub-Account {subAccountId} Holdings</h4>
      {holding ? (
        <ul>
          {holding.map((item: any) => (
            <li key={item.token}>
              {item.token}: {item.holding}
            </li>
          ))}
        </ul>
      ) : (
        <p>No holdings found.</p>
      )}
    </div>
  );
};

import { useSubAccountMaxWithdrawal } from '@orderly.network/hooks';

export const WithdrawalLimit = ({ subAccountId, token }: { subAccountId: number, token: string }) => {
  // Calculates the maximum amount that can be withdrawn from a sub-account for a specific token.
  const { data, isLoading } = useSubAccountMaxWithdrawal(subAccountId, token);

  const maxWithdraw = data?.max_withdraw || '0';

