import { useSubAccountWS } from '@orderly.network/hooks';
import { useEffect } from 'react';

export const SubAccountConnector = ({ subAccountId }: { subAccountId: number }) => {
  // Manages the WebSocket connection specifically for sub-account data streams.
  const { connected, connect, disconnect } = useSubAccountWS();

  useEffect(() => {
    // Ensure connection is active when viewing a specific sub-account
    connect();
    return () => {
      // Cleanup if necessary, though SDK often handles this globally
      // disconnect(); 
    };
  }, [connect]);

  return (
    <div className="flex items-center gap-2">
      <div className={`w-3 h-3 rounded-full ${connected ? 'bg-green-500' : 'bg-red-500'}`} />
      <span>Sub-Account {subAccountId} Feed: {connected ? 'Live' : 'Offline'}</span>
    </div>
  );
};

