import { useStatisticsDaily } from "@orderly.network/hooks";

export const DailyStatsCard = () => {
  // Fetches aggregated daily trading statistics
  // Returns: vol (Volume), pnl (Profit and Loss), roi (Return on Investment)
  const { data, isLoading } = useStatisticsDaily();

  if (isLoading) return <div>Loading stats...</div>;

  return (
    <div className="grid grid-cols-3 gap-4 p-4 bg-white shadow rounded-lg">
      <StatCard label="Volume (24h)" value={data?.vol} prefix="$" />
      <StatCard label="PnL (24h)" value={data?.pnl} prefix="$" isPnl />
      <StatCard label="ROI (24h)" value={data?.roi} suffix="%" />
    </div>
