};

import { useConvert } from '@orderly.network/hooks';
import { useState } from 'react';

export const TokenConverter = () => {
  const [fromToken, setFromToken] = useState('USDC');
  const [toToken, setToToken] = useState('USDT');
  const [amount, setAmount] = useState('100');

  // Hook to handle conversion logic (e.g. USDC to USDT)
  const { convert, isLoading, error } = useConvert();

  const handleConvert = async () => {
    try {
      const txHash = await convert({
        fromToken,
        toToken,
        amount
      });
      console.log('Conversion successful:', txHash);
    } catch (err) {
      console.error('Conversion failed:', err);
    }
  };

  return (
    <div className="flex flex-col gap-2 p-4 border rounded">
      <input 
        value={amount} 
        onChange={(e) => setAmount(e.target.value)} 
        placeholder="Amount"
        className="border p-2 rounded"
      />
      <div className="flex gap-2">
        <select value={fromToken} onChange={(e) => setFromToken(e.target.value)} className="border p-2">
          <option value="USDC">USDC</option>
          <option value="USDT">USDT</option>
        </select>
        <span>to</span>
        <select value={toToken} onChange={(e) => setToToken(e.target.value)} className="border p-2">
          <option value="USDT">USDT</option>
          <option value="USDC">USDC</option>
        </select>
      </div>
      <button 
        onClick={handleConvert} 
        disabled={isLoading}
        className="bg-blue-600 text-white p-2 rounded disabled:opacity-50"
      >
        {isLoading ? 'Converting...' : 'Convert'}
      </button>
      {error && <p className="text-red-500 text-sm">{error.message}</p>}
    </div>
  );
};

