import { useFundingRatesStore } from '@orderly.network/hooks';

export const FundingTicker = ({ symbol }: { symbol: string }) => {
  // Accesses the global Zustand store for funding rates
  // This allows for high-performance updates without re-fetching
  const getRate = useFundingRatesStore((state) => state.getRate);
  const rates = useFundingRatesStore((state) => state.rates);

  const currentRate = getRate ? getRate(symbol) : rates[symbol];

  return (
    <div className="inline-block px-3 py-1 bg-gray-800 text-white rounded-full text-xs font-mono">
      {symbol}: {currentRate ? `${(currentRate * 100).toFixed(4)}%` : 'Loading...'}
    </div>
  );
};

