export const TransferModal = () => {
  const [amount, setAmount] = useState("");
  // Hook to handle transfers between Wallet and Orderly Account
  const { transfer, isLoading } = useInternalTransfer();

  const handleDeposit = async () => {
    try {
      // Execute transfer (e.g., from Wallet to Trading Account)
      await transfer({
        token: "USDC",
        amount: parseFloat(amount),
        // direction depends on specific SDK implementation, usually implied by context or explicit param
      });
      alert("Transfer successful!");
      setAmount("");
    } catch (e) {
      console.error("Transfer failed", e);
    }
  };

  return (
    <div className="flex gap-2">
      <input 
        type="number" 
        value={amount} 
        onChange={(e) => setAmount(e.target.value)} 
        placeholder="Amount USDC"
        className="border p-2 rounded"
      />
      <button 
        onClick={handleDeposit} 
        disabled={isLoading}
        className="bg-blue-500 text-white px-4 py-2 rounded disabled:opacity-50"
      >
        {isLoading ? "Processing..." : "Deposit"}
      </button>
    </div>
  );
};

