export const FundingRateTicker = ({ symbol }) => {
  // Fetches the current and predicted funding rate for a specific symbol
  const { 
    fundingRate, 
    predictedFundingRate, 
    nextFundingTime 
  } = useFundingRateBySymbol(symbol);

  // Helper to format percentage
