  return (
    <div className="flex flex-col items-end p-2">
      <span className="text-xs text-gray-500">Funding / Predicted</span>
      <div className="flex gap-2 text-sm font-mono">
        <span className={fundingRate >= 0 ? 'text-green-500' : 'text-red-500'}>
          {formatRate(fundingRate)}
        </span>
        <span className="text-gray-400">/</span>
        <span className={predictedFundingRate >= 0 ? 'text-green-500' : 'text-red-500'}>
          {formatRate(predictedFundingRate)}
        </span>
      </div>
      {nextFundingTime && (
        <span className="text-[10px] text-gray-400">
          Next: {new Date(nextFundingTime).toLocaleTimeString()}
        </span>
      )}
    </div>
  );
};

