import { useWS } from '@orderly.network/hooks';
import { useEffect, useState } from 'react';

export const OrderBook = () => {
  // useWS typically returns a function to subscribe or a socket context
  const { on, off, send } = useWS();
  const [bids, setBids] = useState([]);

  useEffect(() => {
    // Define event handler for orderbook updates
    const handleOrderBook = (data) => {
      if (data.topic === 'orderbook') {
        setBids(data.data.bids);
      }
    };

    // Subscribe to the event
    on('orderbook', handleOrderBook);

    // Subscribe to the channel via WebSocket message
    send({
      id: 1,
      event: 'subscribe',
      topic: 'orderbook',
      params: { symbol: 'PERP_ETH_USDC' }
    });

    // Cleanup on unmount
    return () => {
      off('orderbook', handleOrderBook);
    };
  }, [on, off, send]);

  return (
    <div>
      <h3>Order Book Bids</h3>
      <ul>
        {bids.slice(0, 5).map((bid, index) => (
          <li key={index}>{bid[0]} : {bid[1]}</li>
        ))}
      </ul>
    </div>
  );
};

