import { useWithdraw } from '@orderly.network/hooks';
import { useState } from 'react';

export const WithdrawForm = () => {
  // Returns configuration and function to execute on-chain withdrawal
  const { withdraw, isLoading, symbol, decimals, chainId, network } = useWithdraw();
  const [amount, setAmount] = useState('');

  const handleWithdraw = async () => {
    try {
      await withdraw({
        amount: parseFloat(amount),
        // destination address is usually handled by the hook context or passed here
      });
      alert('Withdrawal initiated!');
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="withdraw-card">
      <h3>Withdraw {symbol}</h3>
      <p>Network: {network} (Chain ID: {chainId})</p>
      <p>Decimals: {decimals}</p>

      <input 
        type="number" 
        step={`1e-${decimals}`}
        value={amount} 
        onChange={(e) => setAmount(e.target.value)} 
        placeholder={`Amount in ${symbol}`} 
      />

      <button onClick={handleWithdraw} disabled={isLoading}>
        {isLoading ? 'Processing...' : 'Withdraw'}
      </button>
    </div>
  );
};

import { useLeverage } from "@orderly.network/hooks";
import { useState } from "react";

export const LeverageControl = ({ symbol }: { symbol: string }) => {
  const [localLeverage, setLocalLeverage] = useState(1);

  // Fetches current leverage and provides function to update it
  const { update, isLoading, curLeverage, maxLeverage, leverageLevers } = useLeverage(symbol);

  const handleUpdate = async () => {
    await update(localLeverage);
  };

  return (
    <div className="bg-gray-100 p-4 rounded">
      <div className="flex justify-between mb-2">
        <span>Current Leverage: <b>{curLeverage}x</b></span>
        <span>Max: {maxLeverage}x</span>
      </div>

      <div className="flex gap-2 items-center">
        <input 
          type="range" 
          min="1" 
          max={maxLeverage} 
          step="1" 
          value={localLeverage}
          onChange={(e) => setLocalLeverage(Number(e.target.value))}
          className="w-full"
        />
        <span className="font-mono w-12 text-right">{localLeverage}x</span>
      </div>

      <button 
        onClick={handleUpdate}
        disabled={isLoading || localLeverage === curLeverage}
        className="mt-3 w-full bg-indigo-600 text-white py-2 rounded disabled:bg-gray-400"
      >
        {isLoading ? "Updating..." : "Confirm Leverage"}
      </button>
    </div>
  );
};

